struct Countries: Codable, Equatable {
    let status: String
    let cities: [City]
}

struct City: Codable, Equatable {
    let name: String
    let lat, lon: Double
    let r: Int
    let points: String
}

extension Countries {
    
    static func mock() -> Self {
        return Countries(status: "success", cities: [.mockCity1(), .mockCity2()])
    }
}

